    borderColor: '#3b82f6',
    backgroundColor: '#3b82f6',
    color: 'white'
  },
  // Current Status
  currentStatus: {
    padding: '0.5rem 1rem',
    borderRadius: '6px',
    color: 'white',
    fontSize: '0.875rem',
    fontWeight: '600',
    textTransform: 'uppercase',
    letterSpacing: '0.05em',
    display: 'inline-block'
  },
  // Detail Sections
  detailSection: {
    paddingBottom: '1.25rem',
    borderBottom: '1px solid #f1f5f9'
  },
  detailsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '0.75rem'
  },
  detailItem: {
    fontSize: '0.875rem',
    color: '#6b7280',
    lineHeight: '1.4'
  },
  descriptionBox: {
    padding: '1rem',
    backgroundColor: '#f8fafc',
    borderRadius: '6px',
    border: '1px solid #e2e8f0',
    fontSize: '0.875rem',
    lineHeight: '1.5',
    color: '#6b7280',
    whiteSpace: 'pre-wrap'
  },
  evidenceList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.5rem'
  },
  evidenceItem: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
    padding: '0.75rem',
    backgroundColor: '#f8fafc',
    border: '1px solid #e2e8f0',
    borderRadius: '6px',
    textDecoration: 'none',
    color: '#374151',
    transition: 'all 0.2s ease',
    fontSize: '0.875rem'
  },
  evidenceIcon: {
    fontSize: '0.875rem',
    flexShrink: 0
  },
  evidenceText: {
    flex: '1',
    fontSize: '0.875rem'
  },
  evidenceArrow: {
    color: '#3b82f6',
    fontWeight: 'bold',
    flexShrink: 0
  },
  // No Selection State
  noSelection: {
    padding: '3rem 2rem',
    textAlign: 'center',
    color: '#6b7280'
  },
  noSelectionIcon: {
    fontSize: '3rem',
    marginBottom: '1rem',
    opacity: '0.5'
  },
  noSelectionTitle: {
    fontSize: '1.25rem',
    fontWeight: '600',
    color: '#6b7280',
    margin: '0 0 0.5rem 0'
  },
  noSelectionText: {
    fontSize: '0.875rem',
    margin: '0',
    lineHeight: '1.5'
  },
  // Empty State
  emptyState: {
    padding: '3rem 2rem',
    textAlign: 'center',
    color: '#6b7280'
  },
  emptyIcon: {
    fontSize: '3rem',
    marginBottom: '1rem',
    opacity: '0.5'
  },
  emptyTitle: {
    fontSize: '1.125rem',
    fontWeight: '600',
    color: '#6b7280',
    margin: '0 0 0.5rem 0'
  },
  emptyText: {
    fontSize: '0.875rem',
    margin: '0',
    lineHeight: '1.5'
  },
  // Loading State
  loadingContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '50vh',
    color: '#6b7280',
    padding: '2rem'
  },
  spinner: {
    width: '40px',
    height: '40px',
    border: '4px solid #f3f4f6',
    borderTop: '4px solid #3b82f6',
    borderRadius: '50%',
    animation: 'spin 1s linear infinite',
    marginBottom: '1rem'
  },
  loadingText: {
    fontSize: '1rem',
    margin: '0'
  }
};

// Add CSS animations
const additionalStyles = `
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  /* Hover effects for non-touch devices */
  @media (hover: hover) {
    .report-card:hover {
      border-color: #3b82f6;
      background-color: #f8fafc;
      transform: translateY(-1px);
      box-shadow: 0 4px 6px rgba(0,0,0,0.1);
    }

    .status-button:hover {
      border-color: #3b82f6;
      background-color: #f0f9ff;
    }

    .evidence-item:hover {
      background-color: #e0f2fe;
      border-color: #3b82f6;
      transform: translateX(2px);
    }

    .refresh-button:hover {
      background-color: #059669;
      transform: translateY(-1px);
    }

    .logout-button:hover {
      background-color: #dc2626;
      transform: translateY(-1px);
    }

    .back-button:hover {
      background-color: #f3f4f6;
      border-color: #9ca3af;
    }
  }

  /* Focus styles for accessibility */
  button:focus-visible {
    outline: 2px solid #3b82f6;
    outline-offset: 2px;
  }

  a:focus-visible {
    outline: 2px solid #3b82f6;
    outline-offset: 2px;
    border-radius: 4px;
  }

  /* Responsive adjustments */
  @media (max-width: 640px) {
    .header-main {
      flex-direction: column;
      text-align: center;
      gap: 1rem;
    }

    .header-actions {
      justify-content: center;
      width: 100%;
    }

    .stats-grid {
      grid-template-columns: repeat(2, 1fr);
    }

    .status-buttons {
      flex-direction: column;
    }

    .status-button {
      min-width: auto;
    }

    .details-grid {
      grid-template-columns: 1fr;
    }
  }

  @media (max-width: 480px) {
    .stats-grid {
      grid-template-columns: 1fr;
    }

    .report-card-header {
      flex-direction: column;
      align-items: flex-start;
      gap: 0.5rem;
    }

    .status-container {
      align-self: flex-start;
    }
  }

  /* Ensure no horizontal overflow */
  * {
    box-sizing: border-box;
  }

  html, body {
    overflow-x: hidden;
    width: 100%;
  }
`;

// Inject styles
if (typeof document !== 'undefined') {
  const style = document.createElement('style');
  style.textContent = additionalStyles;
  document.head.appendChild(style);
}
