import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Admin = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedReport, setSelectedReport] = useState(null);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isTablet, setIsTablet] = useState(window.innerWidth >= 768 && window.innerWidth < 1024);
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    underReview: 0,
    resolved: 0
  });
  const navigate = useNavigate();

  // Handle responsive layout
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
      setIsTablet(window.innerWidth >= 768 && window.innerWidth < 1024);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const res = await axios.get('/api/reports');
      setReports(res.data);
      calculateStats(res.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
      alert('Error fetching reports: ' + (error.response?.data?.message || 'Please check if the backend is running'));
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (reportsData) => {
    const stats = {
      total: reportsData.length,
      pending: reportsData.filter(r => r.status === 'pending').length,
      underReview: reportsData.filter(r => r.status === 'under_review').length,
      resolved: reportsData.filter(r => r.status === 'resolved').length
    };
    setStats(stats);
  };

  const updateReportStatus = async (reportId, newStatus) => {
    try {
      await axios.patch(`/api/reports/${reportId}/status`, { status: newStatus });

      const updatedReports = reports.map(report => 
        report._id === reportId ? { ...report, status: newStatus } : report
      );

      setReports(updatedReports);
      calculateStats(updatedReports);

      if (selectedReport && selectedReport._id === reportId) {
        setSelectedReport({ ...selectedReport, status: newStatus });
      }

      alert('Report status updated successfully!');
    } catch (error) {
      alert('Error updating report status: ' + (error.response?.data?.message || 'Something went wrong'));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    delete axios.defaults.headers.common['Authorization'];
    navigate('/');
    alert('You have been logged out successfully.');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return '#f59e0b';
      case 'under_review': return '#3b82f6';
      case 'resolved': return '#10b981';
      default: return '#6b7280';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'pending': return 'PENDING';
      case 'under_review': return 'UNDER REVIEW';
      case 'resolved': return 'RESOLVED';
      default: return status?.toUpperCase() || 'UNKNOWN';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Date not available';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Close details when switching to mobile and selecting a report
  const handleReportSelect = (report) => {
    setSelectedReport(report);
    if (isMobile) {
      // Scroll to top on mobile when selecting report
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.spinner}></div>
        <p style={styles.loadingText}>Loading reports...</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* Header */}
      <header style={styles.header}>
        <div style={styles.headerMain}>
          <div style={styles.headerText}>
            <h1 style={styles.title}>Admin Dashboard</h1>
            <p style={styles.subtitle}>Manage and process all user reports</p>
          </div>
          <div style={styles.headerActions}>
            <button 
              onClick={fetchReports}
              style={styles.refreshButton}
              aria-label="Refresh reports"
            >
              <span style={styles.buttonIcon}>🔄</span>
              {!isMobile && 'Refresh'}
            </button>
            <button 
              onClick={handleLogout}
              style={styles.logoutButton}
              aria-label="Logout"
            >
              <span style={styles.buttonIcon}>🚪</span>
              {!isMobile && 'Logout'}
            </button>
          </div>
        </div>
      </header>

      {/* Statistics Cards */}
      <section style={styles.statsSection}>
        <div style={styles.statsGrid}>
          <div style={styles.statCard}>
            <div style={styles.statIcon}>📊</div>
            <div style={styles.statContent}>
              <div style={styles.statNumber}>{stats.total}</div>
              <div style={styles.statLabel}>Total Reports</div>
            </div>
          </div>
          <div style={styles.statCard}>
            <div style={styles.statIcon}>⏳</div>
            <div style={styles.statContent}>
              <div style={styles.statNumber}>{stats.pending}</div>
              <div style={styles.statLabel}>Pending</div>
            </div>
          </div>
          <div style={styles.statCard}>
            <div style={styles.statIcon}>🔍</div>
            <div style={styles.statContent}>
              <div style={styles.statNumber}>{stats.underReview}</div>
              <div style={styles.statLabel}>Under Review</div>
            </div>
          </div>
          <div style={styles.statCard}>
            <div style={styles.statIcon}>✅</div>
            <div style={styles.statContent}>
              <div style={styles.statNumber}>{stats.resolved}</div>
              <div style={styles.statLabel}>Resolved</div>
            </div>
          </div>
        </div>
      </section>

      {/* Main Content */}
      <main style={styles.main}>
        {/* Mobile/Tablet Layout - Conditional rendering based on selection */}
        {(isMobile || isTablet) ? (
          selectedReport ? (
            /* Details View for Mobile/Tablet */
            <div style={styles.detailsContainer}>
              <div style={styles.detailsHeader}>
                <button 
                  onClick={() => setSelectedReport(null)}
                  style={styles.backButton}
                  aria-label="Back to reports list"
                >
                  ← Back
                </button>
                <h2 style={styles.detailsTitle}>
                  {selectedReport.reportId ? `Report #${selectedReport.reportId}` : `Report ${selectedReport._id?.slice(-6) || 'N/A'}`}
                </h2>
              </div>
              <ReportDetails 
                report={selectedReport} 
                onStatusUpdate={updateReportStatus}
                formatDate={formatDate}
                getStatusColor={getStatusColor}
                getStatusText={getStatusText}
              />
            </div>
          ) : (
            /* List View for Mobile/Tablet */
            <div style={styles.listContainer}>
              <div style={styles.listHeader}>
                <h2 style={styles.sectionTitle}>All Reports ({reports.length})</h2>
              </div>
              <ReportsList 
                reports={reports}
                selectedReport={selectedReport}
                onReportSelect={handleReportSelect}
                formatDate={formatDate}
                getStatusColor={getStatusColor}
                getStatusText={getStatusText}
                isMobile={isMobile}
              />
            </div>
          )
        ) : (
          /* Desktop Layout - Side by side */
          <div style={styles.desktopLayout}>
            {/* Reports List */}
            <div style={styles.listSection}>
              <div style={styles.listHeader}>
                <h2 style={styles.sectionTitle}>All Reports ({reports.length})</h2>
              </div>
              <ReportsList 
                reports={reports}
                selectedReport={selectedReport}
                onReportSelect={setSelectedReport}
                formatDate={formatDate}
                getStatusColor={getStatusColor}
                getStatusText={getStatusText}
                isMobile={false}
              />
            </div>

            {/* Details Panel */}
            <div style={styles.detailsSection}>
              {selectedReport ? (
                <>
                  <div style={styles.detailsHeader}>
                    <h2 style={styles.detailsTitle}>
                      {selectedReport.reportId ? `Report #${selectedReport.reportId}` : `Report ${selectedReport._id?.slice(-6) || 'N/A'}`}
                    </h2>
                  </div>
                  <ReportDetails 
                    report={selectedReport} 
                    onStatusUpdate={updateReportStatus}
                    formatDate={formatDate}
                    getStatusColor={getStatusColor}
                    getStatusText={getStatusText}
                  />
                </>
              ) : (
                <div style={styles.noSelection}>
                  <div style={styles.noSelectionIcon}>📋</div>
                  <h3 style={styles.noSelectionTitle}>Select a Report</h3>
                  <p style={styles.noSelectionText}>Click on a report from the list to view details and update its status.</p>
                </div>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

// Separate Reports List Component
const ReportsList = ({ reports, selectedReport, onReportSelect, formatDate, getStatusColor, getStatusText, isMobile }) => {
  if (reports.length === 0) {
    return (
      <div style={styles.emptyState}>
        <div style={styles.emptyIcon}>📋</div>
        <h3 style={styles.emptyTitle}>No reports found</h3>
        <p style={styles.emptyText}>There are no reports in the system yet.</p>
      </div>
    );
  }

  return (
    <div style={styles.reportsList}>
      {reports.map(report => (
        <div 
          key={report._id} 
          style={{
            ...styles.reportCard,
            ...(selectedReport?._id === report._id ? styles.selectedReportCard : {})
          }}
          onClick={() => onReportSelect(report)}
        >
          <div style={styles.reportCardHeader}>
            <div style={styles.reportCardMain}>
              <h4 style={styles.reportId}>
                {report.reportId ? `Report #${report.reportId}` : `Report ${report._id?.slice(-6) || 'N/A'}`}
              </h4>
              <span style={styles.reportDate}>
                {formatDate(report.createdAt)}
              </span>
            </div>
            <div style={styles.statusContainer}>
              <span 
                style={{
                  ...styles.statusBadge,
                  backgroundColor: getStatusColor(report.status)
                }}
              >
                {getStatusText(report.status)}
              </span>
            </div>
          </div>

          <div style={styles.reportCardBody}>
            <p style={styles.incidentType}>
              <strong>Type:</strong> {report.incidentType ? report.incidentType.replace(/_/g, ' ').toUpperCase() : 'Not specified'}
            </p>
            <p style={styles.reportDescription}>
              {report.description && report.description.length > (isMobile ? 60 : 100) 
                ? `${report.description.substring(0, isMobile ? 60 : 100)}...` 
                : report.description || 'No description provided'}
            </p>
            <div style={styles.reportMeta}>
              <span style={styles.metaItem}>
                👤 {report.isAnonymous ? 'Anonymous' : (report.user?.name || report.userId?.name || 'User')}
              </span>
              <span style={styles.metaItem}>
                📍 {report.location?.address || report.location || 'Location not specified'}
              </span>
              {report.evidence && report.evidence.length > 0 && (
                <span style={styles.metaItem}>
                  📎 {report.evidence.length} file(s)
                </span>
              )}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

// Separate Report Details Component
const ReportDetails = ({ report, onStatusUpdate, formatDate, getStatusColor, getStatusText }) => {
  return (
    <div style={styles.detailsContent}>
      {/* Status Update Section */}
      <section style={styles.actionSection}>
        <h3 style={styles.sectionSubtitle}>Update Status</h3>
        <div style={styles.statusButtons}>
          <button
            onClick={() => onStatusUpdate(report._id, 'pending')}
            style={{
              ...styles.statusButton,
              ...(report.status === 'pending' ? styles.statusButtonActive : {})
            }}
          >
            ⏳ Pending
          </button>
          <button
            onClick={() => onStatusUpdate(report._id, 'under_review')}
            style={{
              ...styles.statusButton,
              ...(report.status === 'under_review' ? styles.statusButtonActive : {})
            }}
          >
            🔍 Under Review
          </button>
          <button
            onClick={() => onStatusUpdate(report._id, 'resolved')}
            style={{
              ...styles.statusButton,
              ...(report.status === 'resolved' ? styles.statusButtonActive : {})
            }}
          >
            ✅ Resolved
          </button>
        </div>
      </section>

      {/* Current Status */}
      <section style={styles.detailSection}>
        <h3 style={styles.sectionSubtitle}>Current Status</h3>
        <div style={{
          ...styles.currentStatus,
          backgroundColor: getStatusColor(report.status)
        }}>
          {getStatusText(report.status)}
        </div>
      </section>

      {/* Report Details */}
      <section style={styles.detailSection}>
        <h3 style={styles.sectionSubtitle}>Report Details</h3>
        <div style={styles.detailsGrid}>
          <div style={styles.detailItem}>
            <strong>Report ID:</strong> {report.reportId ? `#${report.reportId}` : report._id?.slice(-6) || 'N/A'}
          </div>
          <div style={styles.detailItem}>
            <strong>Submitted:</strong> {formatDate(report.createdAt)}
          </div>
          <div style={styles.detailItem}>
            <strong>Incident Type:</strong> {report.incidentType ? report.incidentType.replace(/_/g, ' ').toUpperCase() : 'Not specified'}
          </div>
          <div style={styles.detailItem}>
            <strong>Incident Date:</strong> {formatDate(report.dateTime)}
          </div>
          <div style={styles.detailItem}>
            <strong>Location:</strong> {report.location?.address || report.location || 'Not specified'}
          </div>
          <div style={styles.detailItem}>
            <strong>Anonymous:</strong> {report.isAnonymous ? 'Yes' : 'No'}
          </div>
          <div style={styles.detailItem}>
            <strong>Reported by:</strong> {report.isAnonymous ? 'Anonymous' : (report.user?.name || report.userId?.name || 'User')}
          </div>
          {!report.isAnonymous && (report.user?.email || report.userId?.email) && (
            <div style={styles.detailItem}>
              <strong>User Email:</strong> {report.user?.email || report.userId?.email}
            </div>
          )}
        </div>
      </section>

      {/* Description */}
      <section style={styles.detailSection}>
        <h3 style={styles.sectionSubtitle}>Description</h3>
        <div style={styles.descriptionBox}>
          {report.description || 'No description provided'}
        </div>
      </section>

      {/* Evidence */}
      {report.evidence && report.evidence.length > 0 && (
        <section style={styles.detailSection}>
          <h3 style={styles.sectionSubtitle}>
            Evidence ({report.evidence.length} file(s))
          </h3>
          <div style={styles.evidenceList}>
            {report.evidence.map((file, index) => (
              <a 
                key={index}
                href={`/api/evidence/${file}`}
                target="_blank"
                rel="noopener noreferrer"
                style={styles.evidenceItem}
              >
                <span style={styles.evidenceIcon}>📎</span>
                <span style={styles.evidenceText}>
                  Evidence {index + 1}
                </span>
                <span style={styles.evidenceArrow}>→</span>
              </a>
            ))}
          </div>
        </section>
      )}
    </div>
  );
};

