import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const ReportIncident = ({ user }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    incidentType: '',
    description: '',
    location: '',
    dateTime: '',
    isAnonymous: false
  });
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleFileChange = (e) => {
    setFiles(Array.from(e.target.files));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const submitData = new FormData();
      submitData.append('incidentType', formData.incidentType);
      submitData.append('description', formData.description);
      submitData.append('location', JSON.stringify({
        address: formData.location
      }));
      submitData.append('dateTime', formData.dateTime);
      submitData.append('isAnonymous', formData.isAnonymous);

      files.forEach(file => {
        submitData.append('evidence', file);
      });

      await axios.post('/reports', submitData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      alert('Report submitted successfully!');
      navigate('/my-reports');
    } catch (error) {
      alert('Error submitting report: ' + (error.response?.data?.message || 'Something went wrong'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      {/* Header with Back Button */}
      <div style={styles.header}>
        <Link to="/dashboard" style={styles.backButton}>
          ← Back to Dashboard
        </Link>
        <h1 style={styles.title}>Report Harassment Incident</h1>
        <p style={styles.subtitle}>Your report will be handled with confidentiality and care.</p>
      </div>

      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Incident Type *</label>
          <select 
            name="incidentType" 
            value={formData.incidentType}
            onChange={handleChange}
            required
            style={styles.select}
          >
            <option value="">Select type</option>
            <option value="verbal">Verbal Harassment</option>
            <option value="physical">Physical Harassment</option>
            <option value="online">Online Harassment</option>
            <option value="workplace">Workplace Harassment</option>
            <option value="public">Public Harassment</option>
            <option value="other">Other</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Description *</label>
          <textarea 
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="5"
            placeholder="Please describe the incident in detail..."
            required
            style={styles.textarea}
          />
        </div>

        <div style={styles.formRow}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Location *</label>
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleChange}
              placeholder="Where did the incident occur?"
              required
              style={styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Date and Time *</label>
            <input
              type="datetime-local"
              name="dateTime"
              value={formData.dateTime}
              onChange={handleChange}
              required
              style={styles.input}
            />
          </div>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Evidence (Photos, Videos, Documents)</label>
          <input
            type="file"
            multiple
            onChange={handleFileChange}
            accept="image/*,video/*,.pdf,.doc,.docx"
            style={styles.fileInput}
          />
          <small style={styles.helpText}>You can upload multiple files (max 5)</small>

          {files.length > 0 && (
            <div style={styles.fileList}>
              <strong>Selected files:</strong>
              {files.map((file, index) => (
                <div key={index} style={styles.fileItem}>
                  <span style={styles.fileName}>{file.name}</span>
                  <span style={styles.fileSize}>
                    ({(file.size / 1024 / 1024).toFixed(2)} MB)
                  </span>
                </div>
              ))}
            </div>
          )}
        </div>

        <div style={styles.formGroup}>
          <label style={styles.checkboxLabel}>
            <input
              type="checkbox"
              name="isAnonymous"
              checked={formData.isAnonymous}
              onChange={(e) => setFormData({
                ...formData,
                isAnonymous: e.target.checked
              })}
              style={styles.checkbox}
            />
            Report anonymously
          </label>
          <small style={styles.helpText}>
            Your personal information will not be shared with authorities
          </small>
        </div>

        <div style={styles.buttonGroup}>
          <Link to="/dashboard" style={styles.cancelButton}>
            Cancel
          </Link>
          <button 
            type="submit" 
            style={{
              ...styles.submitButton,
              ...(loading ? styles.submitButtonDisabled : {})
            }}
            disabled={loading}
          >
            {loading ? 'Submitting...' : 'Submit Report'}
          </button>
        </div>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '1rem',
    maxWidth: '800px',
    margin: '0 auto',
    backgroundColor: '#f8f9fa',
    minHeight: '100vh',
    '@media (min-width: 768px)': {
      padding: '2rem'
    }
  },
  header: {
    marginBottom: '2rem',
    textAlign: 'center',
    '@media (min-width: 768px)': {
      textAlign: 'left'
    }
