import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Login = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    role: 'user'
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const endpoint = isLogin ? '/auth/login' : '/auth/register';

      // Debug logging
      console.log('üîç === LOGIN/REGISTER DEBUG INFO ===');
      console.log('üìù Mode:', isLogin ? 'LOGIN' : 'REGISTER');
      console.log('üåê Base URL:', axios.defaults.baseURL);
      console.log('üîó Full Endpoint:', axios.defaults.baseURL + endpoint);
      console.log('üì¶ Request Data:', formData);
      console.log('üîÑ Making request...');

      const res = await axios.post(endpoint, formData);

      console.log('‚úÖ SUCCESS - Response received:', res.data);
      console.log('üîë Token received:', res.data.token ? 'Yes' : 'No');
      console.log('üë§ User data received:', res.data.user ? 'Yes' : 'No');

      onLogin(res.data.token, res.data.user);
    } catch (error) {
      console.log('‚ùå ERROR DETAILS:');
      console.log('üö® Error object:', error);
      console.log('üì° Error response:', error.response);
      console.log('üî¥ Error status:', error.response?.status);
      console.log('üìÑ Error data:', error.response?.data);
      console.log('üìã Error message:', error.response?.data?.message);
      console.log('üåê Network error?', error.message);

      setError(error.response?.data?.message || 'Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  // ... rest of the component remains the same
  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.header}>
          <h2>{isLogin ? 'Welcome Back' : 'Create Account'}</h2>
          <p style={styles.subtitle}>
            {isLogin ? 'Sign in to your SafeReport account' : 'Join SafeReport to report incidents safely'}
          </p>
        </div>

        {error && (
          <div style={styles.error}>
            ‚ö†Ô∏è {error}
          </div>
        )}

        <form onSubmit={handleSubmit} style={styles.form}>
          {!isLogin && (
            <>
              <div style={styles.formGroup}>
                <label style={styles.label}>Full Name *</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  style={styles.input}
                  placeholder="Enter your full name"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Phone Number</label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder="Enter your phone number"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Account Type</label>
                <select 
                  name="role" 
                  value={formData.role} 
                  onChange={handleChange}
                  style={styles.select}
                >
                  <option value="user">Regular User</option>
                  <option value="counselor">Counselor/Support Staff</option>
                </select>
                <small style={styles.helpText}>
                  {formData.role === 'counselor' 
                    ? 'Counselors can provide support to users' 
                    : 'Regular users can report incidents and access support'}
                </small>
              </div>
            </>
          )}

          <div style={styles.formGroup}>
            <label style={styles.label}>Email Address *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              style={styles.input}
              placeholder="Enter your email address"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Password *</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              style={styles.input}
              placeholder="Enter your password"
              minLength="6"
            />
            <small style={styles.helpText}>
              Password must be at least 6 characters long
            </small>
          </div>

          {isLogin && (
            <div style={styles.forgotPassword}>
              <Link to="/forgot-password" style={styles.forgotLink}>
                Forgot your password?
              </Link>
            </div>
          )}

          <button 
            type="submit" 
            style={{
              ...styles.submitButton,
              ...(loading ? styles.submitButtonDisabled : {})
            }}
            disabled={loading}
          >
            {loading ? 'Please wait...' : (isLogin ? 'Sign In' : 'Create Account')}
          </button>
        </form>

        <div style={styles.switchContainer}>
          <p style={styles.switchText}>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <span 
              style={styles.switchLink}
              onClick={() => {
                setIsLogin(!isLogin);
                setError('');
              }}
            >
              {isLogin ? 'Sign up now' : 'Sign in here'}
            </span>
          </p>
        </div>

        <div style={styles.features}>
          <h4 style={styles.featuresTitle}>Why join SafeReport?</h4>
          <ul style={styles.featuresList}>
            <li>üõ°Ô∏è Report incidents anonymously</li>
            <li>üîí Secure and confidential platform</li>
            <li>üì± Track your report status</li>
            <li>ü§ù Access support resources</li>
            <li>üó∫Ô∏è Help create safer communities</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

// ... styles remain the same
const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '80vh',
    padding: '2rem 1rem',
    backgroundColor: '#f8f9fa'
  },
  card: {
    background: 'white',
    padding: '2.5rem',
    borderRadius: '12px',
    boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: '450px'
  },
  header: {
    textAlign: 'center',
    marginBottom: '2rem'
  },
  subtitle: {
    color: '#666',
    marginTop: '0.5rem'
  },
  error: {
    backgroundColor: '#ffe6e6',
    color: '#d63031',
    padding: '12px',
    borderRadius: '6px',
    marginBottom: '1.5rem',
    textAlign: 'center',
    border: '1px solid #ffcccc'
  },
  form: {
    marginBottom: '1.5rem'
  },
  formGroup: {
    marginBottom: '1.5rem'
  },
  label: {
    display: 'block',
    marginBottom: '0.5rem',
    fontWeight: '600',
    color: '#333'
  },
  input: {
    width: '100%',
    padding: '12px 16px',
    border: '1px solid #ddd',
    borderRadius: '6px',
    fontSize: '16px',
