import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Admin = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedReport, setSelectedReport] = useState(null);
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    underReview: 0,
    resolved: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const res = await axios.get('/reports');
      setReports(res.data);
      calculateStats(res.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
      alert('Error fetching reports. Please check your backend connection.');
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (reportsData) => {
    const stats = {
      total: reportsData.length,
      pending: reportsData.filter(r => r.status === 'pending').length,
      underReview: reportsData.filter(r => r.status === 'under_review').length,
      resolved: reportsData.filter(r => r.status === 'resolved').length
    };
    setStats(stats);
  };

  const updateReportStatus = async (reportId, newStatus) => {
    try {
      await axios.patch(`/reports/${reportId}/status`, { status: newStatus });
      const updatedReports = reports.map(report => 
        report._id === reportId ? { ...report, status: newStatus } : report
      );
      setReports(updatedReports);
      calculateStats(updatedReports);
      if (selectedReport && selectedReport._id === reportId) {
        setSelectedReport({ ...selectedReport, status: newStatus });
      }
      alert('Report status updated successfully!');
    } catch (error) {
      alert('Error updating report status.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    delete axios.defaults.headers.common['Authorization'];
    navigate('/');
    alert('Logged out successfully.');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return '#ffc107';
      case 'under_review': return '#17a2b8';
      case 'resolved': return '#28a745';
      default: return '#6c757d';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'pending': return 'PENDING';
      case 'under_review': return 'UNDER REVIEW';
      case 'resolved': return 'RESOLVED';
      default: return status?.toUpperCase() || 'UNKNOWN';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Date not available';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric', month: 'short', day: 'numeric',
      hour: '2-digit', minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.spinner}></div>
        <p>Loading reports...</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* Header */}
      <div className="header">
        <div style={styles.headerContent}>
          <h1 style={styles.title}>Admin Dashboard</h1>
          <p style={styles.subtitle}>Manage and process all user reports</p>
        </div>
        <div className="headerActions">
          <button onClick={fetchReports} style={styles.refreshButton}>üîÑ Refresh</button>
          <button onClick={handleLogout} style={styles.logoutButton}>üö™ Logout</button>
        </div>
      </div>

      {/* Stats */}
      <div className="statsGrid">
        {[
          { icon: 'üìä', label: 'Total Reports', value: stats.total },
          { icon: '‚è≥', label: 'Pending', value: stats.pending },
          { icon: 'üîç', label: 'Under Review', value: stats.underReview },
          { icon: '‚úÖ', label: 'Resolved', value: stats.resolved }
        ].map((stat, i) => (
          <div key={i} className="statCard">
            <div className="statIcon">{stat.icon}</div>
            <div>
              <div className="statNumber">{stat.value}</div>
              <div className="statLabel">{stat.label}</div>
            </div>
          </div>
        ))}
      </div>

      {/* Main Content */}
      <div className="content">
        <div className="reportsSection">
          <h2 style={styles.sectionTitle}>All Reports ({reports.length})</h2>
          <div className="reportsList">
            {reports.length === 0 ? (
              <div style={styles.emptyState}>No reports found.</div>
            ) : (
              reports.map(report => (
                <div
                  key={report._id}
                  className={`reportItem ${selectedReport?._id === report._id ? 'selected' : ''}`}
                  onClick={() => setSelectedReport(report)}
                >
                  <div style={styles.reportHeader}>
                    <div>
                      <h4 style={styles.reportId}>
                        {report.reportId ? `Report #${report.reportId}` : `Report ${report._id?.slice(-6)}`}
                      </h4>
                      <span style={styles.reportDate}>{formatDate(report.createdAt)}</span>
                    </div>
                    <span style={{
                      ...styles.statusBadge,
                      backgroundColor: getStatusColor(report.status)
                    }}>
                      {getStatusText(report.status)}
                    </span>
                  </div>
                  <p><strong>Type:</strong> {report.incidentType || 'Not specified'}</p>
                  <p>{report.description?.slice(0, 100) || 'No description'}</p>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Report Details */}
        {selectedReport && (
          <div className="detailsSection">
            <div className="detailsHeader">
              <h2>{selectedReport.reportId ? `Report #${selectedReport.reportId}` : `Report ${selectedReport._id?.slice(-6)}`}</h2>
              <button onClick={() => setSelectedReport(null)} style={styles.closeButton}>√ó</button>
            </div>
            <div style={styles.detailsContent}>
              <h3>Status</h3>
              <div className="statusButtons">
                {['pending', 'under_review', 'resolved'].map(st => (
                  <button
                    key={st}
                    onClick={() => updateReportStatus(selectedReport._id, st)}
                    className={`statusButton ${selectedReport.status === st ? 'active' : ''}`}
                  >
                    {getStatusText(st)}
                  </button>
                ))}
              </div>
              <h3>Description</h3>
              <div style={styles.descriptionBox}>{selectedReport.description}</div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

