  statusBadge: { padding: '4px 8px', borderRadius: '8px', color: 'white', fontWeight: 'bold', fontSize: '0.75rem' },
  closeButton: { border: 'none', background: 'transparent', fontSize: '1.5rem', cursor: 'pointer' },
  detailsContent: { display: 'flex', flexDirection: 'column', gap: '1rem' },
  descriptionBox: { background: '#f8f9fa', padding: '1rem', borderRadius: '6px', border: '1px solid #e9ecef' },
  emptyState: { textAlign: 'center', color: '#6c757d', padding: '2rem' }
};

