import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Admin = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedReport, setSelectedReport] = useState(null);
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    underReview: 0,
    resolved: 0
  });

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const res = await axios.get('/reports');
      setReports(res.data);
      calculateStats(res.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (reportsData) => {
    const stats = {
      total: reportsData.length,
      pending: reportsData.filter(r => r.status === 'pending').length,
      underReview: reportsData.filter(r => r.status === 'under_review').length,
      resolved: reportsData.filter(r => r.status === 'resolved').length
    };
    setStats(stats);
  };

  const updateReportStatus = async (reportId, newStatus) => {
    try {
      await axios.patch(`/reports/${reportId}/status`, { status: newStatus });

      // Update local state
      const updatedReports = reports.map(report => 
        report._id === reportId ? { ...report, status: newStatus } : report
      );

      setReports(updatedReports);
      calculateStats(updatedReports);

      if (selectedReport && selectedReport._id === reportId) {
        setSelectedReport({ ...selectedReport, status: newStatus });
      }

      alert('Report status updated successfully!');
    } catch (error) {
      alert('Error updating report status: ' + (error.response?.data?.message || 'Something went wrong'));
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return '#ffc107';
      case 'under_review': return '#17a2b8';
      case 'resolved': return '#28a745';
      default: return '#6c757d';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return '#dc3545';
      case 'medium': return '#ffc107';
      case 'low': return '#28a745';
      default: return '#6c757d';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.spinner}></div>
        <p>Loading reports...</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <Link to="/dashboard" style={styles.backButton}>
          <span style={styles.backIcon}>â†</span>
          Back to Dashboard
        </Link>
        <div style={styles.headerContent}>
          <h1 style={styles.title}>Admin Dashboard</h1>
          <p style={styles.subtitle}>Manage and process harassment reports</p>
        </div>
      </div>

      {/* Statistics */}
      <div style={styles.statsGrid}>
        <div style={styles.statCard}>
          <div style={styles.statIcon}>ğŸ“Š</div>
          <div style={styles.statInfo}>
            <div style={styles.statNumber}>{stats.total}</div>
            <div style={styles.statLabel}>Total Reports</div>
          </div>
        </div>
        <div style={styles.statCard}>
          <div style={styles.statIcon}>â³</div>
          <div style={styles.statInfo}>
            <div style={styles.statNumber}>{stats.pending}</div>
            <div style={styles.statLabel}>Pending</div>
          </div>
        </div>
        <div style={styles.statCard}>
          <div style={styles.statIcon}>ğŸ”</div>
          <div style={styles.statInfo}>
            <div style={styles.statNumber}>{stats.underReview}</div>
            <div style={styles.statLabel}>Under Review</div>
          </div>
        </div>
        <div style={styles.statCard}>
          <div style={styles.statIcon}>âœ…</div>
          <div style={styles.statInfo}>
            <div style={styles.statNumber}>{stats.resolved}</div>
            <div style={styles.statLabel}>Resolved</div>
          </div>
        </div>
      </div>

      <div style={styles.content}>
        {/* Reports List */}
        <div style={styles.reportsSection}>
          <h2 style={styles.sectionTitle}>All Reports</h2>
          <div style={styles.reportsList}>
            {reports.length === 0 ? (
              <div style={styles.emptyState}>
                <div style={styles.emptyIcon}>ğŸ“‹</div>
                <h3>No reports found</h3>
                <p>When users submit reports, they will appear here.</p>
              </div>
            ) : (
              reports.map(report => (
                <div 
                  key={report._id} 
                  style={{
                    ...styles.reportItem,
                    ...(selectedReport?._id === report._id ? styles.selectedReport : {})
                  }}
                  onClick={() => setSelectedReport(report)}
                >
                  <div style={styles.reportHeader}>
                    <div style={styles.reportBasicInfo}>
                      <h4 style={styles.reportId}>Report #{report.reportId}</h4>
                      <span style={styles.reportDate}>
                        {formatDate(report.createdAt)}
                      </span>
                    </div>
                    <div style={styles.statusSection}>
                      <span 
                        style={{
                          ...styles.statusBadge,
                          backgroundColor: getStatusColor(report.status)
                        }}
                      >
                        {report.status.replace('_', ' ').toUpperCase()}
                      </span>
                      {report.priority && (
                        <span 
                          style={{
                            ...styles.priorityBadge,
                            backgroundColor: getPriorityColor(report.priority)
                          }}
                        >
                          {report.priority.toUpperCase()}
                        </span>
                      )}
                    </div>
                  </div>

                  <div style={styles.reportPreview}>
                    <p style={styles.incidentType}>
                      <strong>Type:</strong> {report.incidentType}
                    </p>
                    <p style={styles.reportDescription}>
                      {report.description.length > 100 
                        ? `${report.description.substring(0, 100)}...` 
                        : report.description}
                    </p>
                    <div style={styles.reportMeta}>
                      <span style={styles.metaItem}>
                        ğŸ‘¤ {report.isAnonymous ? 'Anonymous' : (report.userId?.name || 'Unknown User')}
                      </span>
                      <span style={styles.metaItem}>
                        ğŸ“ {report.location?.address || 'Location not specified'}
                      </span>
                      {report.evidence && report.evidence.length > 0 && (
                        <span style={styles.metaItem}>
                          ğŸ“ {report.evidence.length} file(s)
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Report Details */}
        {selectedReport && (
          <div style={styles.detailsSection}>
            <div style={styles.detailsHeader}>
              <h2 style={styles.detailsTitle}>
                Report #{selectedReport.reportId}
              </h2>
              <button 
                onClick={() => setSelectedReport(null)}
                style={styles.closeButton}
              >
                Ã—
              </button>
            </div>

            <div style={styles.detailsContent}>
              {/* Status Update Section */}
              <div style={styles.actionSection}>
                <h3 style={styles.sectionSubtitle}>Update Status</h3>
                <div style={styles.statusButtons}>
                  <button
                    onClick={() => updateReportStatus(selectedReport._id, 'pending')}
                    style={{
                      ...styles.statusButton,
                      ...(selectedReport.status === 'pending' ? styles.statusButtonActive : {})
                    }}
                  >
                    â³ Pending
                  </button>
                  <button
                    onClick={() => updateReportStatus(selectedReport._id, 'under_review')}
                    style={{
                      ...styles.statusButton,
                      ...(selectedReport.status === 'under_review' ? styles.statusButtonActive : {})
                    }}
                  >
                    ğŸ” Under Review
                  </button>
                  <button
                    onClick={() => updateReportStatus(selectedReport._id, 'resolved')}
                    style={{
                      ...styles.statusButton,
                      ...(selectedReport.status === 'resolved' ? styles.statusButtonActive : {})
                    }}
                  >
                    âœ… Resolved
                  </button>
                </div>
              </div>

              {/* Report Details */}
              <div style={styles.detailSection}>
                <h3 style={styles.sectionSubtitle}>Report Details</h3>
                <div style={styles.detailGrid}>
                  <div style={styles.detailItem}>
                    <strong>Report ID:</strong> #{selectedReport.reportId}
                  </div>
                  <div style={styles.detailItem}>
                    <strong>Submitted:</strong> {formatDate(selectedReport.createdAt)}
                  </div>
                  <div style={styles.detailItem}>
                    <strong>Incident Type:</strong> {selectedReport.incidentType}
                  </div>
                  <div style={styles.detailItem}>
                    <strong>Incident Date:</strong> {formatDate(selectedReport.dateTime)}
                  </div>
                  <div style={styles.detailItem}>
                    <strong>Location:</strong> {selectedReport.location?.address || 'Not specified'}
                  </div>
                  <div style={styles.detailItem}>
                    <strong>Anonymous:</strong> {selectedReport.isAnonymous ? 'Yes' : 'No'}
                  </div>
                  <div style={styles.detailItem}>
                    <strong>Reported by:</strong> {selectedReport.isAnonymous ? 'Anonymous' : (selectedReport.userId?.name || 'Unknown User')}
                  </div>
                  {!selectedReport.isAnonymous && selectedReport.userId?.email && (
                    <div style={styles.detailItem}>
