import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';

// Components
import Landing from './pages/Landing';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import ReportIncident from './pages/ReportIncident';
import MyReports from './pages/MyReports';
import Admin from './pages/Admin';

// Set axios base URL
axios.defaults.baseURL = 'http://localhost:5000/api';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchUser();
    } else {
      setLoading(false);
    }
  }, []);

  const fetchUser = async () => {
    try {
      const res = await axios.get('/auth/me');
      setUser(res.data.user);
    } catch (error) {
      localStorage.removeItem('token');
      delete axios.defaults.headers.common['Authorization'];
    }
    setLoading(false);
  };

  const login = (token, userData) => {
    localStorage.setItem('token', token);
    axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    setUser(userData);

    // Redirect based on role after login
    if (userData.role === 'counselor' || userData.role === 'admin') {
      return <Navigate to="/admin" />;
    } else {
      return <Navigate to="/dashboard" />;
    }
  };

  const register = (token, userData) => {
    localStorage.setItem('token', token);
    axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    setUser(userData);

    // After registration, redirect to login for regular users
    // For counselors/admins, redirect directly to admin panel
    if (userData.role === 'counselor' || userData.role === 'admin') {
      return <Navigate to="/admin" />;
    } else {
      // For regular users, redirect to login page
      localStorage.removeItem('token');
      delete axios.defaults.headers.common['Authorization'];
      setUser(null);
      return <Navigate to="/login" />;
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setUser(null);
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        fontSize: '1.2rem'
      }}>
        Loading...
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Public routes */}
          <Route path="/" element={<Landing />} />
          <Route 
            path="/login" 
            element={!user ? <Login onLogin={login} /> : (user.role === 'counselor' || user.role === 'admin') ? <Navigate to="/admin" /> : <Navigate to="/dashboard" />} 
          />
          <Route 
            path="/register" 
            element={!user ? <Register onRegister={register} /> : (user.role === 'counselor' || user.role === 'admin') ? <Navigate to="/admin" /> : <Navigate to="/dashboard" />} 
          />

          {/* Protected routes */}
          <Route 
            path="/dashboard" 
            element={user ? (user.role === 'counselor' || user.role === 'admin') ? <Navigate to="/admin" /> : <Dashboard user={user} /> : <Navigate to="/login" />} 
          />
          <Route 
            path="/report" 
            element={user ? (user.role === 'counselor' || user.role === 'admin') ? <Navigate to="/admin" /> : <ReportIncident user={user} /> : <Navigate to="/login" />} 
          />
          <Route 
            path="/my-reports" 
            element={user ? (user.role === 'counselor' || user.role === 'admin') ? <Navigate to="/admin" /> : <MyReports user={user} /> : <Navigate to="/login" />} 
          />
          <Route 
            path="/admin" 
            element={user && (user.role === 'counselor' || user.role === 'admin') ? <Admin /> : <Navigate to="/dashboard" />} 
          />

          {/* Catch all route */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
