// Enhanced responsive styles
const responsiveStyles = `
  @media (min-width: 480px) {
    .statsOverview {
      justify-content: space-around;
    }

    .statItem {
      max-width: 140px;
    }
  }

  @media (min-width: 640px) {
    .container {
      padding: '1.5rem';
    }

    .header {
      padding: '2rem 1.5rem';
    }

    .greeting {
      font-size: '2.25rem';
    }

    .subtitle {
      font-size: '1.1rem';
    }

    .section {
      padding: '0 1rem';
    }

    .quickActions {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 1.5rem;
    }

    .actionCard {
      padding: 1.5rem;
    }

    .resourcesGrid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
      gap: 1.5rem;
    }

    .resourceCard {
      padding: 1.75rem;
    }

    .networkGrid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
      gap: 1.5rem;
    }
  }

  @media (min-width: 768px) {
    .container {
      padding: '2rem';
    }

    .header {
      flex-direction: row;
      justify-content: space-between;
      align-items: flex-start;
      padding: '2.5rem 2rem';
    }

    .welcomeSection {
      text-align: left;
      flex: 1;
    }

    .greeting {
      font-size: '2.5rem';
    }

    .userName {
      display: inline;
    }

    .headerRight {
      align-items: flex-end;
      width: auto;
    }

    .statsOverview {
      width: auto;
      justify-content: flex-end;
    }

    .statItem {
      min-width: 120px;
      max-width: none;
    }

    .logoutButton {
      width: auto;
    }

    .sectionTitle {
      text-align: left;
      font-size: '1.8rem';
    }

    .actionTitle {
      font-size: '1.3rem';
    }

    .actionDescription,
    .tipItem,
    .resourceItem,
    .contactName,
    .contactNumber,
    .networkDescription {
      font-size: '1rem';
    }
  }

  @media (min-width: 1024px) {
    .header {
      margin-bottom: '3rem';
    }

    .section {
      margin-bottom: '3rem';
    }

    .quickActions {
      grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    }

    .resourcesGrid {
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    }
  }

  .actionCard:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 20px rgba(0,0,0,0.15);
    border-color: #007bff;
  }

  .logoutButton:hover {
    background-color: #c82333;
  }
`;

// Inject styles
if (typeof document !== 'undefined') {
  const style = document.createElement('style');
  style.textContent = responsiveStyles;
  document.head.appendChild(style);
}
export default Dashboard;
