import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';

// Set axios base URL
axios.defaults.baseURL = 'http://localhost:5000/api';

// Simple Landing component defined inline
const Landing = () => {
  return (
    <div style={{ 
      minHeight: '100vh',
      padding: '2rem',
      backgroundColor: '#f0f8ff',
      textAlign: 'center'
    }}>
      <h1 style={{ color: '#2c3e50', fontSize: '3rem' }}>SafeReport</h1>
      <p style={{ fontSize: '1.5rem', color: '#34495e' }}>
        Women Harassment Reporting System
      </p>
      <div style={{ marginTop: '2rem' }}>
        <a 
          href="/login" 
          style={{
            backgroundColor: '#007bff',
            color: 'white',
            padding: '12px 30px',
            textDecoration: 'none',
            borderRadius: '5px',
            fontSize: '1.1rem'
          }}
        >
          Get Started
        </a>
      </div>
    </div>
  );
};

// Simple Navbar
const Navbar = ({ user, onLogout }) => {
  return (
    <nav style={{
      backgroundColor: '#2c3e50',
      padding: '1rem',
      color: 'white'
    }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <a href="/" style={{ color: 'white', textDecoration: 'none', fontSize: '1.5rem' }}>
          SafeReport
        </a>
      </div>
    </nav>
  );
};

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      // For now, skip user fetch to test landing page
      setLoading(false);
    } else {
      setLoading(false);
    }
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <div className="App">
        <Navbar user={user} onLogout={() => {}} />
        <Routes>
          <Route path="/" element={<Landing />} />
          <Route path="/login" element={<div>Login Page - Coming Soon</div>} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
