  }

  .evidenceItem:hover {
    background-color: #e9ecef;
    border-color: #007bff;
  }

  .logoutButton:hover {
    background-color: #c82333;
  }

  .refreshButton:hover {
    background-color: #218838;
  }

  /* Mobile-first: Details panel takes full width when selected */
  @media (max-width: 767px) {
    .detailsSection {
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      z-index: 1000;
      border-radius: 0;
      margin: 0;
    }

    .noSelection {
      display: none;
    }

    .mobileSelectionPrompt {
      display: block;
    }
  }

  /* Tablet and Desktop */
  @media (min-width: 768px) {
    .header {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
      text-align: left;
    }

    .headerContent {
      text-align: left;
    }

    .headerActions {
      justify-content: flex-end;
    }

    .statsGrid {
      grid-template-columns: repeat(4, 1fr);
    }

    .content {
      flex-direction: row;
    }

    .reportsSection {
      flex: 1;
      order: 1;
      max-height: 70vh;
    }

    .detailsSection,
    .noSelection {
      flex: 2;
      order: 2;
      max-height: 70vh;
    }

    .reportHeader {
      flex-direction: row;
      justify-content: space-between;
      align-items: flex-start;
    }

    .statusSection {
      align-items: flex-end;
    }

    .detailGrid {
      grid-template-columns: repeat(2, 1fr);
    }

    .mobileSelectionPrompt {
      display: none;
    }
  }

  @media (min-width: 1024px) {
    .container {
      padding: '2rem';
    }

    .header {
      margin-bottom: '2rem';
    }

    .reportsSection {
      padding: '1.5rem';
    }

    .detailsSection {
      padding: '1.5rem';
    }

    .reportItem {
      padding: '1.25rem';
    }

    .statCard {
      padding: '1.5rem';
    }
  }

  @media (max-width: 480px) {
    .headerActions {
      flex-direction: column;
      width: 100%;
    }

    .refreshButton,
    .logoutButton {
      width: 100%;
    }

    .statsGrid {
      grid-template-columns: 1fr;
    }

    .statusButtons {
      flex-direction: column;
    }

    .statusButton {
      min-width: auto;
    }

    .evidenceItem {
      flex-direction: column;
      text-align: center;
      gap: 0.5rem;
    }

    .evidenceText {
      text-align: center;
    }
  }

  @media (max-width: 360px) {
    .container {
      padding: '0.5rem';
    }

    .header,
    .reportsSection,
    .detailsSection {
      padding: '1rem';
    }

    .reportItem {
      padding: '0.75rem';
    }

    .statCard {
      padding: '1rem';
    }
  }
`;

