import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ReportIncident = ({ user }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    incidentType: '',
    description: '',
    location: '',
    dateTime: '',
    isAnonymous: false
  });
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleFileChange = (e) => {
    setFiles(Array.from(e.target.files));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const submitData = new FormData();
      submitData.append('incidentType', formData.incidentType);
      submitData.append('description', formData.description);
      submitData.append('location', JSON.stringify({
        address: formData.location
      }));
      submitData.append('dateTime', formData.dateTime);
      submitData.append('isAnonymous', formData.isAnonymous);

      files.forEach(file => {
        submitData.append('evidence', file);
      });

      await axios.post('/reports', submitData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      alert('Report submitted successfully!');
      navigate('/my-reports');
    } catch (error) {
      alert('Error submitting report: ' + (error.response?.data?.message || 'Something went wrong'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1>Report Harassment Incident</h1>
      <p>Your report will be handled with confidentiality and care.</p>

      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label>Incident Type *</label>
          <select 
            name="incidentType" 
            value={formData.incidentType}
            onChange={handleChange}
            required
          >
            <option value="">Select type</option>
            <option value="verbal">Verbal Harassment</option>
            <option value="physical">Physical Harassment</option>
            <option value="online">Online Harassment</option>
            <option value="workplace">Workplace Harassment</option>
            <option value="public">Public Harassment</option>
            <option value="other">Other</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label>Description *</label>
          <textarea 
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="5"
            placeholder="Please describe the incident in detail..."
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label>Location *</label>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            placeholder="Where did the incident occur?"
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label>Date and Time *</label>
          <input
            type="datetime-local"
            name="dateTime"
            value={formData.dateTime}
            onChange={handleChange}
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label>Evidence (Photos, Videos, Documents)</label>
          <input
            type="file"
            multiple
            onChange={handleFileChange}
            accept="image/*,video/*,.pdf,.doc,.docx"
          />
          <small>You can upload multiple files (max 5)</small>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.checkboxLabel}>
            <input
              type="checkbox"
              name="isAnonymous"
              checked={formData.isAnonymous}
              onChange={(e) => setFormData({
                ...formData,
                isAnonymous: e.target.checked
              })}
            />
            Report anonymously
          </label>
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          disabled={loading}
        >
          {loading ? 'Submitting...' : 'Submit Report'}
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem 0',
  },
  form: {
    maxWidth: '600px',
    marginTop: '2rem',
  },
  formGroup: {
    marginBottom: '1.5rem',
  },
  checkboxLabel: {
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
  },
  submitBtn: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '12px 30px',
    border: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    cursor: 'pointer',
  }
};

export default ReportIncident;
